//Call to routes to delete the respective files
$(document).ready(function(){
    $('.cast-crew-multiple').select2();
});
$(function(){      
    $('.time-duration').inputmask(
        "hh:mm:ss", {
        placeholder: "HH:MM:SS", 
        insertMode: false, 
        showMaskOnHover: false,
        hourFormat: 12
    });
});
$(document).on('click','.minimal-red', function(){
    var is_series = $("input[name='is_series']:checked").val();
    if(is_series == 1){
        $('.main-video-switch').hide();
    }
    else{
        $('.main-video-switch').show();
    }
});
//LNSEL
//delete subscription
$('#subscriptionDeleteModal').on('show.bs.modal', function (e) {
    var baseUrl         =   $('#baseUrl').val();
    var deleteId =   $(e.relatedTarget).attr('data-subscription-id');
    var url             =   baseUrl+'subscription/deleteSubscription/'+deleteId;
    $(this).find('#deletesubscription').attr('href', url);
});
